import { Component, OnInit } from "@angular/core";
import { ApiService } from "./api.service";
import { FormsModule } from "@angular/forms";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent implements OnInit {
  clientes: any[] = [];
  clientesMorosos: any[] = [];
  clienteParaEditar: any = null;
  clientesToDisplay: any[] = []; // Variable para mostrar clientes según el botón seleccionado

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.getAllClientes();
  }

  getAllClientes() {
    this.apiService.getAllClientes().subscribe((data: any) => {
      this.clientes = data;
    });
  }

  reloadClientes() {
    this.getAllClientes();
  }

  editarCliente(cliente: any) {
    // Abre la pantalla de edición con los datos del cliente seleccionado
    this.clienteParaEditar = { ...cliente };
  }

  cancelarEdicion() {
    // Cierra la pantalla de edición
    this.clienteParaEditar = null;
  }

  guardarEdicion() {
    // Envía los cambios al servidor y actualiza la lista de clientes
    this.apiService.updateCliente(this.clienteParaEditar).subscribe(() => {
      this.getAllClientes();
      this.cancelarEdicion();
    });
  }

  eliminarCliente(clienteId: number) {
    // Envía una solicitud para eliminar el cliente y actualiza la lista de clientes
    if (confirm("¿Estás seguro de que deseas eliminar este cliente?")) {
      this.apiService.deleteCliente(clienteId).subscribe(() => {
        this.getAllClientes();
      });
    }
  }
  mostrarMorosos() {
    // Filtrar los clientes morosos (deben dinero)
    this.clientesMorosos = this.clientes.filter((cliente) => cliente.deuda > 0);
  }
  mostrarTodos() {
    this.clientesToDisplay = this.clientes; // Muestra todos los clientes
  }
}
