import { Component, OnInit } from "@angular/core";
import { ApiService } from "../api.service";

@Component({
  selector: "app-clientes",
  templateUrl: "./clientes.component.html",
  styleUrls: ["./clientes.component.css"],
})
export class ClientesComponent implements OnInit {
  clientes: any[] = [];
  clientesFiltrados: any[] = [];
  filtro: string = "todos"; // Por defecto, muestra todos los clientes

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    this.apiService.getAllClientes().subscribe((data: any[]) => {
      this.clientes = data;
      this.clientesFiltrados = this.clientes; // Inicialmente muestra todos los clientes
    });
  }

  filtrarClientes(filtro: string) {
    if (filtro === "morosos") {
      this.clientesFiltrados = this.clientes.filter(
        (cliente) => cliente.deuda > 0
      );
    } else if (filtro === "sin-deuda") {
      this.clientesFiltrados = this.clientes.filter(
        (cliente) => cliente.deuda === 0
      );
    } else {
      this.clientesFiltrados = this.clientes; // Mostrar todos
    }
  }

  modificarCliente(cliente: any) {
    this.clienteParaEditar = { ...cliente };
  }

  eliminarCliente(id: number) {
    // Agrega aquí la lógica para eliminar un cliente por su ID
  }
}
